sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if ( newValue == '') {
		return;
	}
}
var whatneeded = g_form.getValue('u_what_needed');

if(newValue == 'hr'){
   g.form.addOption('u_what_needed', 'hr1', 'Human Resources 1'); 
g.form.addOption('u_what_needed', 'hr2', 'Human Resources 2');
g.form.addOption('u_what_needed', 'other', 'Other');
}

if(newValue=='facilities'){
	g.form.addOption('u_what_needed', 'facilities1', 'Facilities 1');
	g.form.addOption('u_what_needed', 'facilities2', 'Facilities 2');
	g.form.addOption('u_what_needed', 'facilities3', 'Facilities 3');
}

if(newValue == 'legal') {
g.form.addOption('u_what_needed', 'legal1', 'Legal 1');
	g.form.addOption('u_what_needed', 'legal2', 'Legal 2');
	g.form.addOption('u_what_needed', 'legal3', 'Legal 3');
}

if(isLoading && !g_form.isNewRecord()) {
	g_form.setValue('u_what_needed' , whatneeded);
}
